# Preverimo, ali je hitrejsi .split(" ") ali .replace(" ", "") -> len
from time import perf_counter

def split(str):
    return(len(str.split(" ")))

def replace(str):
    a = len(str)
    b = len(str.replace(" ", ""))
    return(a-b + 1)

